<style lang="scss">
footer {


  .footer {

    &__nav-wrapper {
      display: grid;
      grid-template-columns: 1fr 1fr;
      gap: 32px;

      &-tel {
        grid-column: 2 / 4;
        grid-row: 2 / 3;
      }
    }

    &__menu-items {
      margin: 10px 0 40px;
      display: grid;
      grid-template-columns: repeat(5, 1fr);
      @include up($md) {
        column-gap: 25px;
      }
      @include up($lg) {
        column-gap: 48px;
      }
    }

    &__menu-head {
      ul {
        li {
          text-transform: none;
          font-size: 10px;
          line-height: 140%;
          @include up($md) {
            font-size: 13px;
            line-height: 150%;
          }

        }
      }

      a {
        &:hover {
          color: $primary-1 !important;
        }

        &:active {
          color: $primary-2 !important;
        }

        &:disabled {
          color: $mid-grey !important;
        }
      }
    }

    &__menu-list {
      margin-top: 20px;

      li {
        &:not(:last-child) {
          margin-bottom: 15px;
        }
      }
    }

    &__inner {
      background-color: $bg;
      padding: 32px 0 20px;
      margin-top: -1px;
    }

    &__contact {
      display: block;
      font-weight: 700;
      font-size: 16px;
      line-height: 130%;
      letter-spacing: 0.03em;
      text-transform: uppercase;
      color: $primary-2 !important;
      @include up($md) {
        font-size: 14px;
        line-height: 150%;
      }
      &:hover {
        color: $primary-1 !important;
      }

      &--mail {
        margin-bottom: 24px;
        @include up($md) {
          font-size: 35px;
          line-height: 48px;
          letter-spacing: 0.05em;
          display: flex;
          margin-top: 40px;
          width: 530px;
          margin-bottom: 40px;
        }
        @include up($lg) {
          font-size: 80px;
          margin: 100px 0;
        }
      }

    }
  }

  img {
    display: block;
    width: 100%;
    @include up($md) {
      margin-top: -102px;
    }
    @include up($lg) {
      margin-top: -152px;
    }
  }

  .logo {
    margin: 0 auto;
    @include up($md) {
      margin: 0 70px 0 0;
    }
    path {
      fill: $primary-1;
    }

  }

  .menu-items {
    font-weight: 400;
    letter-spacing: 0.03em;
    column-count: 2;

    a {
      color: $dark-grey2 !important;
    }

    @include up($md) {
      column-count: unset;
    }

    &__list {
      @include up($md) {
        display: flex;
      }
    }

    &__item {
      color: $primary-2 !important;

      &:not(:last-child) {
        margin-bottom: 32px;
        @include up($md) {
          margin-right: 68px;
        }
      }

    }
  }

  .social-items {
    &__item {
      &:not(:nth-child(3)) {
        margin-right: 80px;
        @include up($md) {
          margin-right: 30px;
        }
      }
    }

    &__item:last-child {
      display: none;

    }
  }

  .pn-logo {
    path {
      fill: $secondary-2;
    }

    &:hover {
      path {
        fill: $primary-2;
        @include transition();
      }
    }

  }
}
</style>
<script>
import MenuItems from "@/components/menu-items";
import SocialItems from "@/components/social-items";
import ContactForm from "@/components/contact-form";

export default {
  components: {SocialItems, MenuItems, ContactForm},
  data() {
    return {
      /* items: [
         {
           name: "Services",
           route: "services",
         },
         {
           name: "About us",
           route: "about-us",

         },
         {
           name: "Projects",
           route: "projects",

         },
         {
           name: "Contacts",
           route: "contacts",

         },
         {
           name: "Benefits",
           route: "benefits",
         },
       ],*/
    }
  },
  computed: {
    items() {
      return this.$store.state.common?.menus;
    },
    year() {
      return new Date().getFullYear();
    },
    phones() {
      return this.$store.state.common.phones?.map(el => ({
        phone: el.content,
        href: "tel:" + el.content.replace(/[^0-9]/g, '')
      }))
    },
    email() {
      return this.$store.state.common.email?.content;
    },
  },
  methods: {
    textToUpperCase(text) {
      return text[0].toUpperCase() + text.slice(1);
    }
  }
}
</script>
